import { useState, useContext, handleSubmit } from "react";
import { Container, Form, } from "react-bootstrap";


export default function UpdatePassword(){

        // State to hold form data and response messages
        const [currentPassword, setCurrentPassword] = useState("");
        const [newPassword, setNewPassword] = useState("");
        const [confirmPassword, setConfirmPassword] = useState("");
        const [message, setMessage] = useState("");
        const [error, setError] = useState("");

        function UpdatePassword (e){
            e.preventDefault();

            // Basic validation
    if (newPassword !== confirmPassword) {
        setError("New passwords do not match.");
        return;
      }
  
      if (newPassword.length < 6) {
        setError("New password must be at least 6 characters.");
        return;
      }

            fetch("http://localhost:4000/users/register", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",  // We are sending JSON data
                    "Authorization": `Bearer ${localStorage.getItem("authToken")}`, // Send JWT token from localStorage
                },
                body: JSON.stringify({
                    currentPassword,  // The current password entered by the user
                    newPassword,      // The new password entered by the user
                  }),
                });

        }

        return (
            <Container className="profile-page mt-5 col-6">
          <h1 className="text-center">Update Your Password</h1>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="oldPassword" className='mb-3'>
              <Form.Label>Old Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your old password"
                value={oldPassword}
                onChange={(e) => setOldPassword(e.target.value)}
                required
              />
            </Form.Group>
    
            <Form.Group controlId="newPassword" className='mb-3'>
              <Form.Label>New Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter new password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
            </Form.Group>
    
            <Form.Group controlId="confirmPassword" className='mb-3'>
              <Form.Label>Confirm New Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Confirm new password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </Form.Group>
    
            {errorMessage && <Alert variant="danger">{errorMessage}</Alert>}
            {successMessage && <Alert variant="success">{successMessage}</Alert>}
    
            <Button variant="primary" type="submit" disabled={isLoading} className="w-100 mt-3">
              {isLoading ? <Spinner animation="border" size="sm" /> : 'Update Password'}
            </Button>
          </Form>
        </Container>
        );
    }
